from selenium import webdriverfrom bs4 import BeautifulSoupoption = webdriver.ChromeOptions()#option.binary_location = '/usr/bin/google-chrome-unstable'option.add_argument('headless')driver = webdriver.Chrome(chrome_options=option)driver.get('https://www.google.com.tw/')ele = driver.find_element_by_name('q')ele.clear()ele.send_keys('sherlock')ele.submit()soup = BeautifulSoup(driver.page_source)def open(url):    option = webdriver.ChromeOptions()    option.add_argument('headless')    driver = webdriver.Chrome(chrome_options=option)    driver.get('https://www.google.com.tw/' + url)    moresoup = BeautifulSoup(driver.page_source)    print_getlink(moresoup)def print_getlink(soup):    for i in soup.findAll('h3', {'class': 'r'}):        links = i.find('a')        all = links.attrs['href']        print(all)        #i.text for text    print('--------')    link = soup.find('a', {'id': 'pnnext'}).attrs['href']    open(link)print_getlink(soup)#driver.close()